export class HexgateError extends Error {
    kind;
    constructor(message, kind) {
        super(message);
        Error.captureStackTrace(this, this.constructor);
        this.kind = kind;
    }
    get name() {
        return this.constructor.name;
    }
    get [Symbol.toStringTag]() {
        return this.constructor.name;
    }
}
