import { CERTIFICATE } from '../../constants/certificate.js';
import { getTokens } from './tokens.js';
export async function auth(options) {
    const tokenList = (await getTokens()).map((tokens) => ({
        ...tokens,
        certificate: options?.certificate || CERTIFICATE
    }));
    if (options?.all) {
        return tokenList;
    }
    else {
        return tokenList[0];
    }
}
