export class Heartbeat {
    fn;
    ms;
    interval = null;
    constructor(fn, ms = 2000) {
        this.fn = fn;
        this.ms = ms;
    }
    stop() {
        if (this.interval) {
            clearInterval(this.interval);
            this.interval = null;
        }
        return this;
    }
    start() {
        this.stop();
        this.interval = setInterval(this.fn, this.ms);
        return this;
    }
}
